INSERT INTO `open_positions` VALUES (1,'Paketleme Elemanı','Garaj Gıda Pazarlama ve İnşaat Sanayi Ticaret A.Ş. bünyesinde çalışmak üzere paketleme elemanı aranmaktadır.\n        \n        İş Tanımı:\n        - Ürünlerin paketlenmesi ve etiketlenmesi\n        - Kalite kontrol süreçlerine destek\n        - Üretim hattında çalışma\n        - Hijyen kurallarına uyum','Üretim','- Lise mezunu\n        - Vardiyalı çalışmaya uygun\n        - Fiziksel olarak sağlıklı\n        - Takım çalışmasına yatkın\n        - Hijyen kurallarına uyum','İstanbul','Tam Zamanlı','Asgari ücret + yan haklar',1,1,'2025-07-18 11:53:29','2025-07-18 11:53:29','https://www.kariyer.net/is-ilani/paketleme-elemani-2847392'),(2,'Gıda Proje Yöneticisi/Yönetmeni','Garaj Gıda Pazarlama ve İnşaat Sanayi Ticaret A.Ş. bünyesinde çalışmak üzere gıda proje yöneticisi aranmaktadır.\n        \n        İş Tanımı:\n        - Gıda projelerinin planlanması ve yönetimi\n        - Ar-Ge çalışmalarının koordinasyonu\n        - Kalite süreçlerinin takibi\n        - Tedarikçi ilişkileri yönetimi\n        - Maliyet analizi ve bütçe yönetimi','Proje Yönetimi','- Gıda Mühendisliği veya ilgili bölüm mezunu\n        - En az 3 yıl proje yönetimi deneyimi\n        - PMP sertifikası tercih edilir\n        - İleri düzey MS Office bilgisi\n        - İngilizce bilgisi\n        - Analitik düşünme yeteneği','İstanbul','Tam Zamanlı','Deneyime göre pazarlık edilir',1,1,'2025-07-18 11:53:29','2025-07-18 11:53:29',NULL),(3,'Soğuk Aşçı','Garaj Gıda Pazarlama ve İnşaat Sanayi Ticaret A.Ş. bünyesinde çalışmak üzere soğuk aşçı aranmaktadır.\n        \n        İş Tanımı:\n        - Soğuk yemek hazırlama\n        - Salata ve mezeler hazırlama\n        - Garnitür hazırlama\n        - Mutfak hijyenine uyum\n        - Menü planlama süreçlerine destek','Mutfak','- Aşçılık sertifikası\n        - En az 2 yıl soğuk aşçılık deneyimi\n        - Hijyen sertifikası\n        - Vardiyalı çalışmaya uygun\n        - Yaratıcı ve titiz','İstanbul','Tam Zamanlı','Deneyime göre pazarlık edilir',1,1,'2025-07-18 11:53:29','2025-07-18 11:53:29','https://www.kariyer.net/is-ilani/soguk-asci-2847390'),(4,'Satın Alma Uzmanı','Garaj Gıda Pazarlama ve İnşaat Sanayi Ticaret A.Ş. bünyesinde çalışmak üzere satın alma uzmanı aranmaktadır.\n        \n        İş Tanımı:\n        - Tedarikçi araştırması ve değerlendirmesi\n        - Fiyat karşılaştırması ve müzakere\n        - Satın alma süreçlerinin yönetimi\n        - Stok takibi ve optimizasyonu\n        - Tedarikçi ilişkileri yönetimi','Satın Alma','- Üniversite mezunu (tercihen İşletme, Endüstri Mühendisliği)\n        - En az 2 yıl satın alma deneyimi\n        - Müzakere becerileri\n        - SAP bilgisi tercih edilir\n        - İngilizce bilgisi\n        - Analitik düşünme yeteneği','İstanbul','Tam Zamanlı','Deneyime göre pazarlık edilir',1,1,'2025-07-18 11:53:29','2025-07-18 11:53:29','https://www.kariyer.net/is-ilani/satin-alma-uzmani-2847389'),(5,'Bulaşıkçı','Garaj Gıda Pazarlama ve İnşaat Sanayi Ticaret A.Ş. bünyesinde çalışmak üzere bulaşıkçı aranmaktadır.\n        \n        İş Tanımı:\n        - Mutfak gereçlerinin yıkanması\n        - Mutfak temizliği\n        - Hijyen kurallarına uyum\n        - Mutfak personeline destek','Mutfak','- Eğitim şartı yok\n        - Vardiyalı çalışmaya uygun\n        - Fiziksel olarak sağlıklı\n        - Temizlik ve hijyen konusunda titiz\n        - Takım çalışmasına yatkın','İstanbul','Tam Zamanlı','Asgari ücret + yan haklar',1,1,'2025-07-18 11:53:29','2025-07-18 11:53:29','https://www.kariyer.net/is-ilani/bulasikci-2847388'),(6,'Depo Elemanı','Garaj Gıda Pazarlama ve İnşaat Sanayi Ticaret A.Ş. bünyesinde çalışmak üzere depo elemanı aranmaktadır.\n        \n        İş Tanımı:\n        - Mal kabul ve sevk işlemleri\n        - Depo düzeni ve organizasyonu\n        - Stok sayımı ve takibi\n        - Forklift kullanımı\n        - Kargo ve nakliye süreçleri','Lojistik','- Lise mezunu\n        - Forklift sertifikası\n        - En az 1 yıl depo deneyim